import React from 'react';

class BadgesList extends React.Component {
    render(){
        return(
            <ul className="list-unstyled">
                {this.props.state.data.map(badge => {
                    return(
                        <li key={badge.id}>
                            <p>
                                {badge.firstName}
                            </p>
                        </li>
                    )
                })}
            </ul>
        );
    }
}

export default BadgesList;
