import React from 'react';

import './styles/BadgeNew.css';
import header from "../images/platziconf-logo.svg";
import NavBar from '../components/Navbar';
import Badge from '../components/Badge';
import BadgeForm from '../components/BadgeForm'

class BadgeNew extends React.Component {

  state = {
    form: {
      firstName: '',
      lastname: '',
      email: '',
      jobtitle: '',
      twitter: '',
    }
  };

  handleChange = e => {
     
    this.setState({
      //Ocurre un problema, el form se esta sobre escribiendo, para que esto no ocurra.
      //obtenemos los valores generados por el primer input, luego lo descomponemos
      //En caso tenga otro nombre, se agregar치 al anterior atributo o reemplazar치
      form:{
        ...this.state.form,
        [e.target.name]: e.target.value,
      }
    })
  }

  render(){
    return(
      <React.Fragment>
          <div className="BadgeNew__hero">
            <img className="BadgeNew__hero-img img_fluid" src={header} alt="Logo" />
          </div>

          <div className="container">
              <div className="row">
                  <div className="col-6">
                    <Badge 
                      firstName={this.state.form.firstName || 'First Name'} 
                      lastName={this.state.form.lastname || 'Last Name'}
                      jobTitle={this.state.form.jobtitle || 'Job Title'}
                      twitter={this.state.form.twitter || 'twitter'}
                      email={this.state.form.email || 'email'}
                    />
                  </div>
                  <div className="col-6">
                    <BadgeForm 
                    onChange={this.handleChange}
                    onSubmit={this.handleSubmit}
                    formValues={this.state.form} />
                  </div>
              </div>
          </div>
      </React.Fragment>
    );
  }
}

export default BadgeNew;
