import React from 'react';
import './styles/BadgeDetails.css'
import confLog from '../images/platziconf-logo.svg'
import api from '../api';
import PageLoading from '../components/PageLoading';
import PageError from '../components/PageError';


class BadgeDetails extends React.Component{

    state = {
        loading: false,
        error: null,
        data: undefined,
    };

    componentDidMount(){
        this.fetchData();
    }

    fetchData = async e => {
        this.setState({
            loading: true,error: null,});

        try{
            const data = await api.badges.read(
                this.props.match.params.badgeId
            );
            this.setState({loading: false, data: data});
        }catch(error){
            this.setState({loading: false, error: error});
        }
    }

    render(){
        if(this.state.loading){
            return <PageLoading />
        }
        if(this.state.error){
            return <PageError error={this.state.error}/>
        }

        const badge = this.state.data;
        console.log(badge.firstName)

        return(            
            <div>
                <div className="BadgeDetails__hero">
                    <div className="container">
                        <div className="row">
                            <div className="col-6 container-image">
                                <img src={confLog} alt="Logo de la conferencia" />
                            </div>
                            <div className="col-6 container-image BadgeDetails__hero-attendant-name" >
                                {/* <h1>{a.firstName} {a.lastName}</h1> */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default BadgeDetails;
