import React from 'react';
import { Link } from 'react-router-dom'

import './styles/BadgeDetails.css'
import confLog from '../images/platziconf-logo.svg'
import api from '../api';
import PageLoading from '../components/PageLoading';
import PageError from '../components/PageError';
import Badge from '../components/Badge';

class BadgeDetails extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            loading: false,
            error: null,
            data: undefined,
        };
    }

    componentDidMount(){
        this.fetchData();
    }

    fetchData = async e => {
        this.setState({
            loading: true,error: null,});

        try{
            const data = await api.badges.read(
                this.props.match.params.badgeId
            );
            this.setState({loading: false, data: data});
        }catch(error){
            this.setState({loading: false, error: error});
        }
    }

    render(){
        if(this.state.loading || this.state.data){
            return <PageLoading />
        }
        if(this.state.error){
            return <PageError error={this.state.error}/>
        }

        const badge = this.state.data;
  
        return(            
            <div>
                <div className="BadgeDetails__hero">
                    <div className="container">
                        <div className="row">
                            <div className="col-6 container-image">
                                <img src={confLog} alt="Logo de la conferencia" />
                            </div>
                            <div className="col-6 container-image BadgeDetails__hero-attendant-name" >
                                <h1>{badge.firstName} {badge.lastName}</h1>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col">
                        <Badge 
                                firstName={badge.firstName}
                                lastName={badge.lastName}
                                email={badge.email}
                                twitter={badge.twitter}
                                jobTitle={badge.jobTitle}
                            />                          
                        </div>
                        <div className="col">
                            <h2>Actions</h2>
                            <div>
                                <div>
                                    <Link className="btn btn-primary" to={`/badges/${badge.id}/edit`}>
                                        Edit
                                    </Link>
                                    <button className="btn btn-danger mb-4">
                                        Delete
                                    </button>
                                </div> 
                            </div> 
                        </div>                    
                    </div>
                </div>
            </div>
        );
    }
}

export default BadgeDetails;
